PROGRAM TRUCKINGINCLUDE LIBRARYINCLUDE STACKCON BYTE CS=$93; CLEAR SCREENCON WORD MXREC=100CON WORD MXRECFIELD=10CON WORD STACKSIZE=5000   CON WORD BUFFERSIZE=5000 CON WORD TEMPBUFFERSIZE=500BYTE STACK [STACKSIZE]; STACKBYTE BUFFER[BUFFERSIZE]WORD RECORD[MXREC,MXRECFIELD]BYTE TEMP  WORD SP; STACK POINTERWORD BP; BUFFER POINTERWORD RECDISKPTR[MXREC]; RECORDS LOADEDWORD LASTRECORD; LAST BUFFER LOCATION USEDWORD CURRENTPTR; ANYTHING POINTERPROC BUFFINIT; SET BUFFER POINTER 0BEGINBP=0ENDPROC STACKFLUSH; COPY STACK TO BUFFERWORD STRPTRBEGINIF SP=0  RETURNWHILE STRPTR<65535  STRPTR=POP  IF STRPTR<65535    IF (LENSTR(STRPTR)+1) > (BUFFERSIZE-BP)      EXIT"BUFFER OVERFLOW"    MOVSTR STRPTR, BUFFER+BP  BP=BP+LENSTR(STRPTR)+1  ENDFUNC BYTE RECFIND; FIND A RECORD IN STACKARG WORD STRING; STRING TO SEARCH FORARG WORD DATE; DATE TO SEARCH FORARG WORD RESULTS; PTRS TO RECORDS FOUNDBYTE FOUNDWORD PTR; PTR FOR SEARCH; RETURNS NUMBER OF RECORDS FOUNDBEGINIF SP=0; NOTHING IN RAM SO RETURN  RETURN -1FOUND=0PTR=SP; PTR=PTR-2; DECREMENT TO DATAWHILE PTR>0 AND FOUND=0  PTR=PTR-1ENDPROC LOGO; DISPLAY PROGRAM NAMEBEGINPUT CS ;CLEAR SCREENOUTPUT"   MARTIN'S TRUCK ACCOUTANT PROGRAM :)#C#C"PUT NLENDPROC TRIPOWN BYTE TN[11]OWN BYTE ORIGIN[81]OWN BYTE DEST[81]OWN BYTE MILEAGE[5]OWN BYTE PAY[6]BEGINLOGO   OUTPUT"TRIP NUMBER: "GETL TN, 10PUT NLOUTPUT"ORIGIN: "GETL ORIGIN, 80PUT NLOUTPUT"DESTINATION: "GETL DEST,80PUT NLOUTPUT"TOTAL MILES, EMPTY AND LOADED: "GETL MILEAGE, 4PUT NLOUTPUT"$PAY$: "GETL PAY, 5PUT NLENDPROC ROOTMENUBEGINREPEAT  LOGO  OUTPUT"T) ENTER TRIP#C"  OUTPUT"R) REPORT#C"  OUTPUT"S) ENTER SETTLEMENT#C"  OUTPUT"X) ENTER EXPENSE#C"  OUTPUT"F) ENTER FUEL#C"  OUTPUT"N) NEW/CHANGE FILE#C"  OUTPUT"Q) QUIT#C"  ; MENU OPTION CHOICE  CHOOSE GETC    'T' ; ENTER TRIP INFO    TRIP  'R' ; REPORT    NOTHING  'S' ; SETTLEMENT    NOTHING  'X' ; EXPENSES    NOTHING  'F' ; FUEL    NOTHING  'N'    NOTHING  'Q' ; QUIT    EXIT  ELSE    NOTHINGUNTIL 0ENDBEGINSTACKINIT(STACK,STACKSIZE)PUSHSTR"THATS WHAT I SAID"PUSHSTR"LICK THE CATS ASS AND LICK IT WELL!"PUT CS,POPSTRTEMP=GETCPUT POPSTRTEMP=GETCROOTMENU   END