PROGRAM VIEWCAT ; VIEW CATEGORIESINCLUDE LIBRARYINCLUDE CONSWORD FH; FILE HANDLEBYTE BUF[500] ; BUFFERWORD BP ; BUFFER POSWORD BB ; BUFFER BYTESBYTE KEY ; ONE CHAR STORAGEBEGINBP=0FH=OPEN("CATEGORIES",'R')IF FH=0  ABORT"FILE NOT FOUND!#C"BB=GETBLKF(FH, BUF, 500)CLOSE FHREPEAT  OUTPUT #BUF[BP]  KEY=GETKEY  PUT NL  BP=BP+LENSTR(#BUF[BP])UNTIL BP>=BBEND